mapper.select:
select *
from(
	select
	no,subject,
	lag(no,1) over (order by no ) pre_no,
	lag(subject,1) over (order by no ) pre_subject,
	lead(no,1) over (order by no ) next_no,
	lead(subject,1) over (order by no ) next_subject
	from(
    	select no,subject
	    from delivery
	    where indent = 0
		)
	)
where no = #{no}

select no,subject,writer,to_char(wdate, 'YYYY-MM-DD HH24:MI:SS')wdate,indent
	from (
			select no,subject,writer,wdate,indent,rownum r
			from (
				select no,subject,writer,wdate,indent
				from delivery
				where lev = 'S'
				order by ref desc
				)
		)
