mapper.update:
update delivery 
set subject = #{subject},
	content = #{content},
	file1 = #{file1,jdbcType=VARCHAR}
where no = #{no}

mapper.select:
select *
from(
	select
	no,subject,
	lag(no,1) over (order by no ) pre_no,
	lag(subject,1) over (order by no ) pre_subject,
	lead(no,1) over (order by no ) next_no,
	lead(subject,1) over (order by no ) next_subject
	from(
    	select no,subject
	    from delivery
	    where indent = 0
		)
	)
where no = #{no}

select no,subject,writer,to_char(wdate, 'yyyy-mm-dd')wdate,indent
	from (
			select no,subject,writer,wdate,indent,rownum r
			from (
				select no,subject,writer,wdate,indent
				from delivery
				
					
						
						subject like '%'||#{word}||'%'
						
						
						content like '%'||#{word}||'%'
						
						
						writer like '%'||#{word}||'%'
						
					
				
				order by ref desc
				)
		)

where r>=#{sno} and r<=#{eno}
