mapper.select:
		select board_no, subject, ref, writer, wdate, check_read, indent, r
		from(
		select board_no, subject, ref, writer, wdate, check_read, indent,
		rownum r
		from(
		select board_no, subject, ref, writer, wdate, check_read,
		indent
		from board
		where lev='A' and type='상품' and itemNo = #{itemNo}
		order by ref desc,board_no asc
		))
		where
    		
		r >=#{sno} and r<=#{eno}

select
count(*)
from(
select board_no, subject, ref, writer, wdate, check_read,
indent, rownum r
from(
select board_no, subject, ref, writer, wdate,
check_read, indent
from board
where lev='A' and type='상품' and itemNo =
#{itemNo}
order by ref desc,board_no asc
))

select reviewNo, id, content,
picture,itemPicture,itemTitle,height,weight,mySize,star,itemColor,itemSize,itemNo,replyContent
from(
select rownum rnum,reviewNo, id, content,
picture,itemPicture,itemTitle,height,weight,mySize,star,itemColor,itemSize,itemNo,replyContent
from(
select reviewNo, id,r.content,r.picture,i.image as
itemPicture,i.title as
itemTitle,height,weight,mySize,star,itemColor,itemSize,i.itemNo,replyContent
from item i, review r,itemOption o
where r.itemOptionNo =
o.itemOptionNo and o.itemNo = i.itemNo
ORDER BY reviewNo desc
)
where
itemNo=#{itemNo}
)
 where rnum>=#{sno} and rnum<=#{eno}

select
count(*) from item
where type = #{type}

select o.itemOptionNo,i.title as itemTitle,i.image as itemImage,i.price as itemPrice, i.salePrice as itemSalePrice, o.itemSize,o.itemColor,i.itemNo,o.itemCount from item i, itemOption o where i.itemNo = o.itemNo and itemOptionNo in
#{item.value}
order by itemOptionNo

select i.itemNo,o.itemOptionNo, image, title, description, content, price,salePrice, i.type,itemOptionNo,itemColor,itemSize,itemCount,t.parentType from (select * from item where rownum>=#{sno} and rownum<=#{eno}) i , itemOption o,itemType t
i.type =t.type and i.itemNo = o.itemNo and
title like '%'||#{keyword}||'%'
i.itemNo = #{keyword}
and i.type = #{type}
and price-salePrice >= #{price1} and price-salePrice <= #{price2}
order by itemNo desc

		select count(*) from item
		
			
				
					and title like '%'||#{keyword}||'%'
				
				
					and itemNo like #{keyword}
				
				
					i.type like #{type}

mapper.update:
update itemOption
set itemCount = itemCount - #{count}
where itemOptionNo =
#{itemOptionNo}

update itemOption
set itemCount = itemCount + #{count}
where itemOptionNo =
#{itemOptionNo}
