mapper.select:
select reviewNo, id, content, picture,itemPicture,itemTitle,height,weight,mySize,star,itemColor,itemSize,itemNo,ansnum
from(
select reviewNo, id,r.content,r.picture,i.image as itemPicture,i.title as itemTitle,height,weight,mySize,star,rownum rnum,itemColor,itemSize,i.itemNo,ansnum
from item i, review r, itemOption o
where r.itemOptionNo = o.itemOptionNo and o.itemNo = i.itemNo

)
 where rnum>=#{sno} and rnum<=#{eno}
and ansnum=0
ORDER BY reviewNo desc

select reviewNo, id,content,picture,height,weight,mySize,star
from review
where reviewNo = #{reviewNo}
